import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Dashboard() {
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    axios.get('http://localhost/backend/get_users.php')
      .then(response => {
        setUsers(response.data);
      });
  }, []);

  const handleAddUser = () => {
    axios.post('http://localhost/backend/add_user.php', {
      username,
      password
    }).then(response => {
      if (response.data.status === 'success') {
        setUsers([...users, { username, created_at: new Date().toISOString() }]);
        setUsername('');
        setPassword('');
      }
    });
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-4xl mx-auto py-10">
        <h1 className="text-3xl font-bold mb-6 text-center">User Dashboard</h1>
        <div className="mb-6">
          <input
            type="text"
            placeholder="Username"
            className="w-full px-4 py-2 mb-4 border rounded-lg focus:outline-none"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-4 py-2 mb-4 border rounded-lg focus:outline-none"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            onClick={handleAddUser}
            className="w-full bg-green-500 text-white py-2 rounded-lg hover:bg-green-600"
          >
            Add User
          </button>
        </div>
        <div>
          <h2 className="text-2xl font-bold mb-4">User List</h2>
          <ul className="space-y-2">
            {users.map((user, index) => (
              <li key={index} className="p-4 bg-white shadow rounded-lg">
                <p><strong>Username:</strong> {user.username}</p>
                <p><strong>Created At:</strong> {new Date(user.created_at).toLocaleString()}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
